// frontend/src/App.tsx
import React, { useState } from 'react';
import axios from 'axios';
import YouTube from 'react-youtube';
import './App.css'; // Usaremos este archivo para el estilo
import { availableGenres } from './genres'; // AsegÃºrate de crear genres.ts

// Tipos de datos
interface Participant {
  id: number;
  age: number;
  genres: string[];
}
// Interfaz que describe el objeto que retorna tu servidor Node.js
interface PlaylistResponse {
  playlist: string[]; // Esperamos que 'playlist' sea un array de strings (IDs de video)
}

const API_URL = 'http://localhost:5000/api/generate-playlist';

function App() {
  const [participants, setParticipants] = useState<Participant[]>([{ id: Date.now(), age: 25, genres: [] }]);
  const [playlist, setPlaylist] = useState<string[]>([]);
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // --- Funciones de Manejo de Participantes ---

  const addParticipant = () => {
    setParticipants([...participants, { id: Date.now(), age: 25, genres: [] }]);
  };

  const removeParticipant = (id: number) => {
    setParticipants(participants.filter(p => p.id !== id));
  };

  const handleAgeChange = (id: number, age: number) => {
    setParticipants(participants.map(p => p.id === id ? { ...p, age: Number(age) } : p));
  };

  const handleGenreToggle = (id: number, genre: string) => {
    setParticipants(participants.map(p => {
      if (p.id === id) {
        const newGenres = p.genres.includes(genre)
          ? p.genres.filter(g => g !== genre) // Quitar si ya existe
          : [...p.genres, genre]; Â  Â  Â  Â  Â  // Agregar si no existe
        return { ...p, genres: newGenres };
      }
      return p;
    }));
  };

  // --- FunciÃ³n Principal de GeneraciÃ³n ---

  const handleGeneratePlaylist = async () => {
    setError(null);
    setIsLoading(true);
    setPlaylist([]); // Limpiar playlist anterior

    // ValidaciÃ³n bÃ¡sica
    const isValid = participants.every(p => p.age >= 10 && p.genres.length > 0);
    if (!isValid) {
      setError("AsegÃºrate de que todos los participantes tengan una edad vÃ¡lida (mÃ­nimo 10) y al menos un gÃ©nero seleccionado.");
      setIsLoading(false);
      return;
    }

    try {
      const response = await axios.post<PlaylistResponse>(API_URL, { participants });

      const newPlaylist = response.data.playlist;
      if (newPlaylist.length === 0) {
        setError("No se encontraron videos con las preferencias seleccionadas. Intenta con gÃ©neros mÃ¡s generales.");
        setIsLoading(false);
        return;
      }

      setPlaylist(newPlaylist);
      setCurrentVideoIndex(0); // Empezar siempre por el primer video
    } catch (err) {
      setError("Error al conectar con el servidor o la API de mÃºsica. Revisa la consola y el backend.");
    } finally {
      setIsLoading(false);
    }
  };

  // --- Funciones de Reproductor ---

  // FunciÃ³n que se dispara al finalizar un video (o al saltar por error)
  const handleVideoEnd = () => {
    // LÃ³gica de shuffle (aleatoriedad)
    if (playlist.length > 0) {
      const nextIndex = Math.floor(Math.random() * playlist.length);
      setCurrentVideoIndex(nextIndex);
    }
  };

  // ðŸ’¡ FUNCIÃ“N PARA MANEJO DE ERRORES: Salta al siguiente tema si el actual estÃ¡ bloqueado
  const handleVideoError = (error: any) => {
    console.error("Video bloqueado o error de reproducciÃ³n, saltando al siguiente tema...");

    // Llama a la lÃ³gica de salto aleatorio si hay mÃ¡s de un video
    if (playlist.length > 1) {
      handleVideoEnd();
    } else {
      // Mensaje de alerta si el Ãºnico video disponible falla
      alert("El Ãºnico video de la lista estÃ¡ bloqueado. Genera una nueva lista con otros gÃ©neros.");
    }
  };

  // ðŸ’¡ FUNCIÃ“N PARA SALTAR A UN VIDEO ESPECÃFICO AL HACER CLIC EN LA LISTA
  const handlePlaySpecificVideo = (index: number) => {
    setCurrentVideoIndex(index);
  };

  const opts = {
    height: '390',
    width: '100%', // Usamos 100% para que el CSS controle el ancho
    playerVars: {
      autoplay: 1, // Auto-reproducir
      rel: 0, Â  Â // No mostrar videos relacionados al final
      controls: 1, // Mostrar controles
    },
  };

  // --- Renderizado de la AplicaciÃ³n ---

  return (
    <div className="app-container">
      <h1>ðŸŽ§ Generador de Playlists - NelProductions</h1>
      <p className="subtitle">Ingresa la edad y los gÃ©neros favoritos de cada amigo.</p>

      <div className="participants-list">
        {participants.map(p => (
          <div key={p.id} className="participant-card">
            <h3>Participante #{participants.indexOf(p) + 1}</h3>

            <div className="input-group">
              <label>Edad:</label>
              <input
                type="number"
                min="10"
                max="99"
                value={p.age}
                onChange={(e) => handleAgeChange(p.id, parseInt(e.target.value) || 0)}
              />
            </div>

            <div className="genre-selection">
              <label>GÃ©neros Favoritos:</label>
              <div className="genre-tags">
                {availableGenres.map(genre => (
                  <button
                    key={genre}
                    className={`genre-tag ${p.genres.includes(genre) ? 'selected' : ''}`}
                    onClick={() => handleGenreToggle(p.id, genre)}
                  >
                    {genre}
                  </button>
                ))}
              </div>
            </div>

            {participants.length > 1 && (
              <button className="remove-btn" onClick={() => removeParticipant(p.id)}>
                Eliminar Participante
              </button>
            )}
          </div>
        ))}
      </div>

      <div className="action-buttons">
        <button className="add-btn" onClick={addParticipant}>
          + Agregar Otro Amigo
        </button>
        <button
          className="generate-btn"
          onClick={handleGeneratePlaylist}
          disabled={isLoading || participants.length === 0}
        >
          {isLoading ? 'Cargando Playlist...' : 'âœ¨ Generar Lista Aleatoria'}
        </button>
      </div>

      {error && <p className="error-message">ðŸš¨ {error}</p>}

      {/* -------------------------------------------------- */}
      {/* --- NUEVA ESTRUCTURA: Reproductor y Lista Lateral --- */}
      {/* -------------------------------------------------- */}
      {playlist.length > 0 && (
        <div className="player-section">
          <h2>ðŸŽ‰ Â¡MÃºsica Lista! ReproducciÃ³n Aleatoria</h2>
          <p className="subtitle">Reproduciendo video {currentVideoIndex + 1} de {playlist.length}.</p>

          <div className="video-and-list-container">

            {/* COLUMNA 1: REPRODUCTOR DE VIDEO */}
            <div className="player-column">
              <div className="youtube-player-container">
                <YouTube
                  videoId={playlist[currentVideoIndex]}
                  opts={opts}
                  onEnd={handleVideoEnd}
                  onError={handleVideoError}
                />
              </div>
            </div>

            {/* COLUMNA 2: LISTA DE REPRODUCCIÃ“N (PLAYLIST) */}
            <div className="playlist-column">
              <h3>Lista Generada</h3>
              {playlist.map((videoId, index) => (
                <div
                  key={videoId} 
                  className={`playlist-item ${index === currentVideoIndex ? 'playing' : ''}`}
                  onClick={() => handlePlaySpecificVideo(index)}
                >
                  <span className="playlist-item-index">{index + 1}.</span>
                  {/* NOTA: Se muestra el ID para ahorrar cuota de API. */}
                  <span>Video ID: {videoId.substring(0, 10)}...</span>
                </div>
              ))}
            </div>

          </div>
        </div>
      )}
    </div>
  );
}

export default App;
